> setwd("~/Desktop") > NEI <- readRDS("summarySCC_PM25.rds") > SCC <- readRDS("Source_Classification_Code.rds")  > if(!exists("NEI")){NEI <- readRDS("./data/summarySCC_PM25.rds")} > if(!exists("SCC")){SCC <- readRDS("./data/Source_Classification_Code.rds")} > if(!exists("NEISCC")){NEISCC <- merge(NEI, SCC, by="SCC")} > library(ggplot2) > subsetNEI <- NEI[NEI$fips=="24510" & NEI$type=="ON-ROAD",  ] > TotalByYear <- aggregate(Emissions ~ year, subsetNEI, FUN=sum) > png('Plot5.png') > g1 <- ggplot(data=TotalByYear, mapping=aes(factor(year), Emissions))+geom_bar(stat="identity", color="steelblue", fill="steelblue") + labs(x="year", y=expression("Total PM"[2.5]*"Emissions")) + labs(title="Total Emissions from motor vehicle in Baltimore City from 1999 to 2008") + print(g1) 
> dev.off() 

